sp_widget.client_script:
function ScorecardController($uibModal, $scope) {
  var c = this;
  c.openFeedbackModal = openFeedbackModal;
  c.toggleMoreLess = toggleMoreLess;

  c.$onInit = function() {
    activateEventListener();
    c.maxDisplayLength = 125;
  };

  function activateEventListener() {
    $scope.$on('peer_feedback_close', function(e) {
      handleFeedbackClose(e);
    });
  }

  function handleFeedbackClose(e) {
    c.modalInstance.close();
  }

  // open a modal to collect record producer for submitting new feedback
  function openFeedbackModal() {
    c.modalInstance = $uibModal.open({
      templateUrl: 'submitFeedbackModalTemplate',
      scope: $scope
    });
  }

  function toggleMoreLess(feedback) {
    feedback.showMore = !feedback.showMore;
  }
}

